from typing import List


def parse_nested_parens(paren_string: str) -> List[int]:
    max_levels = []
    stack = []
    for i, char in enumerate(paren_string):
        if char == '(':
            stack.append(i)
        elif char == ')':
            if stack:
                max_levels.append(i - stack.pop())
            else:
                max_levels.append(0)  # Account for unmatched closing parenthesis at the end

    return max_levels

from typing import List, Optional

def longest(strings: List[str]) -> Optional[str]:
    if not strings:  # Check if the input list is empty
        return None

    longest_string = strings[0]  # Set the first string as the longest initially
    for s in strings:
        if len(s) > len(longest_string):
            longest_string = s

    return longest_string

def how_many_times(string: str, substring: str) -> int:
    count = 0
    for i in range(len(string) - len(substring) + 1):
        if string[i:i+len(substring)] == substring:
            count += 1
    return count

import math

def largest_divisor(n: int) -> int:
    for i in range(2, math.isqrt(n) + 1):
        if n % i == 0:
            return i
    # If n is a prime number, the largest divisor is n-1 (since no smaller divisor exists)
    return n - 1

def fizz_buzz(n: int) -> int:
    count = 0
    for i in range(1, n):
        if i % 11 == 0 or i % 13 == 0:
            num_str = str(i)
            for digit in num_str:
                if digit == '7':
                    count += 1
    return count

def digitSum(s):
