    for char in s:
        if char.isupper():
            sum_upper += ord(char)
    return sum_upper

def will_it_fly(q, w):
    # Check if the list is palindromic (balanced)
    if q != q[::-1]:
        return False

    # Calculate the sum of the elements in the list
    total_weight = sum(q)

    # Compare the sum with the maximum possible weight
    if total_weight <= w:
        return True
    else:
        return False

def is_prime(num):
