        return False
    for i in range(2, int(num**0.5) + 1):
        if num % i == 0:
            return False
    return True

def count_up_to(n):
    primes = []
    num = 2
    while len(primes) < n:
        if is_prime(num):
            primes.append(num)
        num += 1
    return primes

def choose_num(x, y):
    if x > y:
        return -1  # If x is greater than y, there's no valid range

    # Ensure x and y are positive integers
    if not (isinstance(x, int) and isinstance(y, int) and x > 0 and y > 0):
        return -1

    # Find the biggest even number within the range
    for num in range(y, x - 1, -1):  # Start from y and decrement until we find an even number
        if num % 2 == 0:
            return num  # Return the first even number we find

    return -1  # If no even number found in the range, return -1

def count_nums(arr):
    def sum_of_digits(num):
        # Convert negative numbers to two's complement to get their signed digits
        if num < 0:
            num = abs(num) + (1 << (len(str(abs(num))) * 8 - 1))
        return sum(int(digit) for digit in str(num))

    return sum(1 for num in arr if sum_of_digits(num) > 0)

def is_sorted(lst):
    # Check if the list has more than one duplicate of the same number
    if len(set(lst)) != len(lst):
        return False

    # Check if the list is sorted in ascending order
    for i in range(1, len(lst)):
        if lst[i] < lst[i - 1]:
            return False

    return True

def is_nested(string):
    stack = []
    opening_brackets = ['[', '{', '(']
    closing_brackets = [']', '}', ')']

    for char in string:
        if char in opening_brackets:
            stack.append(char)
        elif char in closing_brackets:
            if not stack or closing_brackets.index(char) != opening_brackets.index(stack.pop()):
                return False

    return len(stack) == 0  # If there are no opening brackets left, it's a valid nested sequence

def is_equal_to_sum_even(n):
    def is_even_sum(a, b, c, d):
        return a + b + c + d == n and a % 2 == 0 and b % 2 == 0 and c % 2 == 0 and d % 2 == 0

    for a in range(1, n//2 + 1):  # Start from 1, since we're looking for positive numbers
        for b in range(a, n//2 + 1):
            for c in range(b, n//2 + 1):
                d = n - (a + b + c)  # Calculate the remaining number to reach n
                if is_even_sum(a, b, c, d):
                    return True
    return False

