from typing import List

def parse_nested_parens(paren_string: str) -> List[int]:
    stack = []
    result = []
    nesting_level = 0

    for char in paren_string:
        if char == '(':
            nesting_level += 1
            stack.append(nesting_level)
        elif char == ')':
            nesting_level -= 1
            if nesting_level < 0:
                raise ValueError("Invalid parentheses")
            result.append(stack.pop())

    return result

def get_positive(l: list):
    positive_numbers = []
    for num in l:
        if num > 0:
            positive_numbers.append(num)
    return positive_numbers

def fizz_buzz(n: int) -> int:
    count = 0
    for i in range(1, n):
        if i % 11 == 0 or i % 13 == 0:
            count += str(i).count('7')
    return count

def digitSum(s):
    """Task: Sum of upper-case characters' ASCII codes in a string"""
    upper_chars = [char for char in s if char.isupper()]
    return sum(ord(char) for char in upper_chars)

def is_palindrome(lst):
    return lst == lst[::-1]

def sum_of_elements(lst):
    return sum(lst)

def will_it_fly(q, w):
    if not is_palindrome(q) or sum_of_elements(q) > w:
        return False
    return True

def hex_key(num):
    primes = {2, 3, 5, 7, 11, 13, 17}  # List of known prime hexadecimal digits

    # Convert the hexadecimal string to decimal and check for each prime digit
    count = 0
    decimal_num = int(num, 16)  # Convert hexadecimal to decimal
    for digit in str(decimal_num):
        if digit in primes:
            count += 1

    return count

def is_prime(num):
    """Helper function to check if a number is prime."""
    if num < 2:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

def count_up_to(n):
    """Function to count up to the first n prime numbers."""
    primes = []
    num = 2
    while len(primes) < n:
        if is_prime(num):
            primes.append(num)
        num += 1
    return primes

def choose_num(x, y):
    # Check if both numbers are positive
    if x <= 0 or y <= 0:
        return -1

    # Iterate through the range from x to y (inclusive)
    for num in range(x, y+1):
        # Check if the number is even
        if num % 2 == 0:
            # Return the biggest even number found
            return num

    # If no even number is found in the range, return -1
    return -1

def count_nums(arr):
    def sum_digits(num):
        if num < 0:
            num = -(-num)  # Convert negative number to its absolute
        return sum(int(digit) for digit in str(abs(num)))

    return sum(1 for num in arr if sum_digits(num) > 0)

def minSubArraySum(nums):
    current_sum = 0
    global_min = float('inf')

    for num in nums:
        current_sum += num
        if current_sum < 0:
            current_sum = 0
        if current_sum < global_min:
            global_min = current_sum

    return global_min

def is_nested(string):
    stack = []
    for char in string:
        if char in ['[', '{', '(']:
            stack.append(char)
        elif char in [']', '}', ')']:
            if not stack or stack.pop() != f'[{char}]' if char in '[]' else f'{char}':
                return False

    return not stack

def is_equal_to_sum_even(n):
    def check_sum(even_numbers):
        return n == sum(num for num in even_numbers) and len(even_numbers) == 4

    even_numbers = [2, 4, 6, 8]  # Known even numbers that sum up to 16 (n for 8)
    for num in (2*i for i in range(1, n // 8 + 1) if 2 * i <= n):  # Check for even numbers up to half of n
        if check_sum(even_numbers + [num]):
            return True

    return False

def x_or_y(n, x, y):
    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if num % i == 0:
                return False
        return True

    if is_prime(n):
        return x
    else:
        return y

def int_to_mini_roman(number):
    roman_values = {
        1000: 'm', 900: 'cm', 500: 'd', 400: 'cd',
        100: 'c', 90: 'xc', 50: 'l', 40: 'xl',
        10: 'x', 9: 'ix', 5: 'v', 4: 'iv', 1: 'i'
    }
    roman_string = ''

    for value, symbol in roman_values.items():
        while number >= value:
            roman_string += symbol
            number -= value

    return roman_string.lower()
